//SPDX-License-Identifier: MIT

pragma solidity =0.7.6;

import {ERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";

contract VaultNFTManager is ERC721Upgradeable {
    /// @dev tokenId for the next vault opened
    uint256 public nextId = 1;

    address public controller;

    modifier onlyController() {
        require(msg.sender == controller);
        _;
    }

    function init(address _controller) public initializer {
        // init nft
        __ERC721_init("Short Opyn Squeeth Position", "sSqueeth");
        controller = _controller;
    }

    /**
     * mint new NFT, create a new vault struct
     */
    function mintNFT(address _recipient) external onlyController returns (uint256 tokenId) {
        // mint NFT
        _mint(_recipient, (tokenId = nextId++));
    }
}

contracts/test/OracleTester.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity =0.7.6;

import {IOracle} from "../interfaces/IOracle.sol";

/**
 * use this contract to test how to get twap from exactly 1 timestamp
 * Since we can't access block.timestamp offchain before sending the tx
 */
contract OracleTester {

  IOracle oracle;

  constructor(address _oracle) {
    oracle = IOracle(_oracle);
  }

  function testGetTwapSince(
    uint256 _sinceTimestamp, 
    address _pool, 
    address _base, 
    address _quote
  ) view external returns (uint256) {
    uint32 period = uint32(block.timestamp - _sinceTimestamp);
    return oracle.getTwap(_pool, _base, _quote, period);
  }

  function testGetTwapSafeSince(
    uint256 _sinceTimestamp, 
    address _pool, 
    address _base, 
    address _quote
  ) view external returns (uint256) {
    uint32 period = uint32(block.timestamp - _sinceTimestamp);
    return oracle.getTwapSafe(_pool, _base, _quote, period);
  }
}
